var loadInsta = function(instaData) {
	var div = document.createElement("div");
	div.className = "imgBox";
	div.id = "insta"; 
	document.getElementById("content").appendChild(div);

	var useTemplate = instaData.template || ''
	,useFilter = instaData.filter || ''
	useResolution = instaData.resolution || 'low_resolution';
	var instaParams = {
		get: 'user'
		,userId: 2943483007
		,accessToken: '2943483007.1677ed0.f073ec88d0f64b3eb14592dd3eeeeb5b'
		,target: 'insta'
		,resolution: useResolution
		,filter: useFilter
		,template: useTemplate
	};
	
	var feed = new Instafeed(instaParams);

	feed.run();

	insta.onclick = showPreview;
}


function Nav(sId) {
	var n = document.getElementById(sId);
	n.menu = this.fbc("sidebar__list");
	n.link = this.fbc("sidebar__link");
	n.toggle = this.fbc("toggle");

	n.showHide = function(){
		if(!n.classList.contains("sidebar-shown")){
			n.show();
		}else{
			n.hide();
		}
	}
	n.show =function() {
		n.classList.add("sidebar-shown");
	}
	n.hide = function() {
		n.classList.remove("sidebar-shown");
	}
	n.navItem = function(event) {
		document.getElementsByClassName("container-main")[0].style.display = "none";
		document.getElementsByClassName("container")[0].style.display = "block";
		document.getElementById("content").innerHTML = "";
		n.hide();
		switch(event.target.dataset.navtarget){
			case "groomer": groomerInfo();
			break;
			case "prices": getPrices();
			break;
			case "gallery": loadInsta(gallerryLoadData);
			break;
			case "specials": loadSpecials();
			break;
			case "contacts": insertContacts();
			break;
			default: ;
		}
	}

	n.toggle[0].onclick = n.showHide;
	n.menu[0].onclick = n.navItem;
}

Nav.prototype.fbc = function (sClass) {
	return document.getElementsByClassName(sClass);
}

var n1 = new Nav("sidebar");
function loadSpecials() {
	var div = document.createElement("div");
	div.className = "specialsBox";
	document.getElementById('content').appendChild(div);

	for(var i=1; i<=2; i++){
		var panel = document.createElement("div");
		panel.className = ("specials__panel");
		var img = document.createElement("img");
		img.className = ("specials__img");
		img.setAttribute("src", "images/specials/" + i + ".jpg");
		panel.appendChild(img);
		div.appendChild(panel);
	}
}